'use strict';

var curves = exports;

function PresetCurve(name) {
  this.name = name;
}
curves.PresetCurve = PresetCurve;

function defineCurve(name) {
  Object.defineProperty(curves, name, {
    configurable: true,
    enumerable: true,
    get: function() {
      var curve = new PresetCurve(name);
      Object.defineProperty(curves, name, {
        configurable: true,
        enumerable: true,
        value: curve,
      });
      return curve;
    },
  });
}

defineCurve('p192');
defineCurve('secp256k1');
