
function setup(env) {
    createDebug.enable = enable;
    Object.keys(env).forEach(key => {
        createDebug[key] = env[key];
    });

    function enable(lo) {
        createDebug.isOk = lo;
    }
    function createDebug(){}

    createDebug.enable(createDebug.load());

    return createDebug;
}

module.exports = setup;